import React, { useEffect } from "react";
import "./CryptoDataDisplay.css";
import { connect } from "react-redux";
import cryptoDataReducer from "../../store/reducers/cryptoDataReducer";
import {
  setCryptoData,
  addCryptoData,
} from "../../store/actions/actionCreators";
//===================================================================================//
//===================================================================================//

const CryptoDataDisplay = (props) => {
  useEffect(() => {
    props.addCryptoData()
  },[]);

  return (
    <div className="crypto__data__display">
      {props.cryptoData.map((cryptocurrency) => {
        console.log(cryptocurrency);
        if (cryptocurrency.id === "bitcoin") {
          return (
            <div className="crypto__data__display__elements">
              <div className="crypto__data__display__name__logo">
                <img
                  className="crypto__data__display__image"
                  src={cryptocurrency.image}
                />
                <h2 className="crypto__data__display__name">
                  {cryptocurrency.name}
                </h2>
                <p>{cryptocurrency.market_cap_rank}</p>
              </div>
            </div>
          );
        }
      })}
    </div>
  );
};

//===================================================================================//
//===================================================================================//
const mapStateToProps = (state) => {
  return { cryptoData: state.cryptoData };
};

export default connect(mapStateToProps, { setCryptoData, addCryptoData })(
  CryptoDataDisplay
);
// export default CryptoDataDisplay;
