import React, { useEffect, useRef } from "react";
import "./CryptoDataDisplay.css";
import { connect } from "react-redux";

import {
  setCryptoData,
  addCryptoData,
  showCryptoData,
} from "../../store/actions/actionCreators";



// <script src="https://widgets.coingecko.com/coingecko-coin-converter-widget.js"></script>
// <coingecko-coin-converter-widget  coin-id="bitcoin" currency="usd" background-color="#ffffff" font-color="#4c4c4c" locale="en"></coingecko-coin-converter-widget>

//===================================================================================//
//===================================================================================//

const CryptoDataDisplay = (props) => {



  const baseCurrencyRef = useRef()
  const quoteCurrencyRef = useRef()
  useEffect(() => {
    props.showCryptoData(props.selectedCryptoDisplay);
  }, []);

  console.log(props);

  return (
    <div className="crypto__data__display">
      {props.cryptoData.map((cryptocurrency, cryptocurrencyIndex, cryptoCurrencyArray) => {
        let allCryptoNames = cryptocurrency.name
        console.log(allCryptoNames)
        if (cryptocurrency.id === props.selectedCryptoDisplay.name) {
          let selectedCryptoData = props.selectedCryptoDisplay.data;


          return (
            <div key={selectedCryptoData.id} className="crypto__data__display__elements">
              {/*======================================================================================================================== */}
              <div className="crypto__data__display__name__logo">
                <img
                  className="crypto__data__display__image"
                  src={selectedCryptoData.image.large}
                />

                <h2 className="crypto__data__display__name">
                  {selectedCryptoData.name}
                </h2>
                <p className="crypto__data__display__symbol">
                  {selectedCryptoData.symbol.toUpperCase()}
                </p>

                <p className="crypto__data__display__rank">
                  Rank #{cryptocurrency.market_cap_rank}
                </p>

                {/* <div className="crypto__data__display__description">
                <h3> What is {selectedCryptoData.name}?</h3>
                <p >{selectedCryptoData.description.en}</p>
                </div> */}
              </div>
              {/*======================================================================================================================== */}
              <div className="crypto__data__display__numerical__data">
                <div className="crypto__data__display__current__price">
                  $
                  {selectedCryptoData.market_data.current_price.usd.toLocaleString()}
                  {/* {selectedCryptoData.market_data.price_change_percentage_24h > 0 ? 'green': 'red'} */}
                  <span
                    className="crypto__data__display__24h__percentage__change"
                    style={{
                      backgroundColor:
                        selectedCryptoData.market_data
                          .price_change_percentage_24h > 0
                          ? "green"
                          : "red",
                    }}
                  >
                    {selectedCryptoData.market_data.price_change_percentage_24h}
                    %
                  </span>
                </div>
                <div>
                  {/*======================================================================================================================== */}
                </div>
              </div>
              <div className="crypto__data__display__currency__converter" >
                {/* <img src={selectedCryptoData.image.thumb}/>
                <span>{selectedCryptoData.name}</span>
                <br/>


                <img src={selectedCryptoData.image.thumb}/>
                <span>United states Dollar</span>
              <input defaultValue={selectedCryptoData.market_data.current_price.usd} placeholder="0" ref={quoteCurrencyRef}/> */}

              <input defaultValue={parseInt(1)} placeholder="0" ref={baseCurrencyRef}/>
              <select>
                  <option>[sele] </option>
              </select>


            <button onClick={()=>{
              console.log(parseInt(baseCurrencyRef) * 2)
            }}>Convert</button>

            <div>

              <span>{}</span>
              {/* {quoteCurrencyRef.current_price.value} */}
              {/* {baseCurrencyRef.current.value} */}
            </div>
              </div>  

              <div className="crypto__data__display__chart" >






              </div>

                    {/* {parseInt(quoteCurrencyRef.current.value) + parseInt(baseCurrencyRef.current.value)}
                    {console.log(quoteCurrencyRef.current.value)} */}
              </div>
          );
        }
      })}
    </div>
  );
};

//===================================================================================//
//===================================================================================//
const mapStateToProps = (state) => {
  return {
    cryptoData: state.cryptoData,
    selectedCryptoDisplay: state.selectedCryptoDisplay,
  };
};

export default connect(mapStateToProps, {
  setCryptoData,
  addCryptoData,
  showCryptoData,
})(CryptoDataDisplay);
// export default CryptoDataDisplay;
