import React, { useEffect } from "react";
import "./CryptoDataDisplay.css";
import { connect } from "react-redux";
import cryptoDataReducer from "../../store/reducers/cryptoDataReducer";
import {
  setCryptoData,
  addCryptoData,
  showCryptoData,
} from "../../store/actions/actionCreators";
//===================================================================================//
//===================================================================================//

const CryptoDataDisplay = (props) => {

  useEffect(()=>{
    props.showCryptoData(props.selectedCryptoDisplay)
  },[])

  console.log(props)
    // console.log(props)


  return (
    <div className="crypto__data__display">


      {props.cryptoData.map((cryptocurrency) => {
        // console.log(cry)
          // let chosenCrytpo = props.addCryptoData(cryptocurrency.id)
          // console.log(chosenCrytpo)
        // console.log(cryptocurrency);
        if (cryptocurrency.id === props.selectedCryptoDisplay.name) {
          let selectedCryptoData =props.selectedCryptoDisplay.data
          console.log(selectedCryptoData)
          return (
            <div className="crypto__data__display__elements">
              <div className="crypto__data__display__name__logo">
                <img
                  className="crypto__data__display__image"
                  // src={cryptocurrency.image}
                  src={selectedCryptoData.image.large}
                />
                <h2 className="crypto__data__display__name">
                  {selectedCryptoData.name}
                </h2>
                <p>Rank{cryptocurrency.market_cap_rank}</p>
              </div>

            </div>
          );
        }
      })}
    </div>
  );
};

//===================================================================================//
//===================================================================================//
const mapStateToProps = (state) => {
  return { 
    cryptoData: state.cryptoData,
    selectedCryptoDisplay: state.selectedCryptoDisplay
  };
};

export default connect(mapStateToProps, { setCryptoData, addCryptoData,showCryptoData })(
  CryptoDataDisplay
);
// export default CryptoDataDisplay;
