import React, {useRef, useEffect} from "react";
import Chart from "chart.js/auto";
import {connect} from "redux-react";
import {getCryptoPriceHistory} from "../../store/actions/actionCreators"
// var Chartjs = require('chart.js');

const CryptoChart = () => {


    const chartRef = useRef()

    useEffect(()=>{
        if(chartRef && chartRef.current){
            const chartInstance = new Chart (chartRef.current, 
                {
                    type: 'line',
                    data: {
                        labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
                        datasets: [{
                            label: '# of Votes',
                            data: [12, 19, 3, 5, 2, 3],
                            backgroundColor: [
                                'rgba(255, 99, 132, 0.2)',
                                'rgba(54, 162, 235, 0.2)',
                                'rgba(255, 206, 86, 0.2)',
                                'rgba(75, 192, 192, 0.2)',
                                'rgba(153, 102, 255, 0.2)',
                                'rgba(255, 159, 64, 0.2)'
                            ],
                            borderColor: [
                                'rgba(255, 99, 132, 1)',
                                'rgba(54, 162, 235, 1)',
                                'rgba(255, 206, 86, 1)',
                                'rgba(75, 192, 192, 1)',
                                'rgba(153, 102, 255, 1)',
                                'rgba(255, 159, 64, 1)'
                            ],
                            borderWidth: 1
                        }]
                    },
                    options: {
                        scales: {
                            y: {
                                beginAtZero: true
                            }
                        }
                    }
                }
                )
        }
    })
  return (
    <div className="crypto__data__display__chart">
      <canvas id="myChart" width='250' height="100" ref={chartRef}></canvas>
    </div>
  );
};

export default connect()(CryptoChart);
