import {useState} from 'react'
import PropTypes from 'prop-types';
import axios from 'axios'
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';





//=============================================================================================================//
//=============================================================================================================//

// async function loginUser(credentials) {
//   // return fetch('http://127.0.0.1:8080/api/signin', {
//   return fetch('/api/signin', {

//     method: 'POST',
//     headers: {
//       'Content-Type': 'application/json'
//     },
//     body: JSON.stringify(credentials)
//   })
//     .then(data => data.json())

//     //  axios.create({
//     //    baseURL: 'api',
//     //    timeout: 5000
//     //  })
//  }

//=============================================================================================================//
//=============================================================================================================//



 const Login = () => {
  const [open, setOpen] = useState(false);
  const [username, setUserName] = useState();
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  //=============================================================================================================//
  //=============================================================================================================//

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  //=============================================================================================================//
  //=============================================================================================================//


  const handleSubmit = async e => {
    e.preventDefault();


    console.log('hello');



    try {
        let success = await axios.post("http://localhost:3001/api/users/login", {

        username: username,
        email:email,
        password:password
            // email: emailRef.current.value,
            // password: passwordRef.current.value,
        })
        console.log(success)

        // console.log(success.data.token);
        // window.localStorage.setItem("jwtToken", success.data.token)
        // props.history.push('/main-page')
    } catch (error) {

        console.log(error);
        // setErrorMessage(true)
    } 
    // return await loginUser({
    //   username,
    //   email,
    //   password
    // }
    // );
    // setToken(token);
    // await axios.post('127.0.0.1:8080/api/signin', signin)

  }
//=============================================================================================================//
//=============================================================================================================//

return (
    <div>

    <span className="login__button" onClick={handleClickOpen}>Login</span>

      <Dialog open={open} onClose={handleClose}  aria-labelledby="form-dialog-title"
       fullWidth={true} maxWidth='xs'
      >


        <DialogContent>
          <DialogTitle id="form-dialog-title"><h1> Log In to your Account<br/>Sign In </h1> </DialogTitle>
          {/* <DialogContentText>
            <h3>New to Crypto Collection? <a href="">Create an Account here.</a></h3>

          </DialogContentText> */}
          <hr/>
          <TextField
            variant='outlined'
            margin="dense"
            id="username"
            label="Username"
            type="email"
            fullWidth
            onChange={e => setUserName(e.target.value)}
          />

          {/* <hr/> */}
          <TextField
            variant='outlined'
            margin="dense"
            id="email"
            label="Email Address"
            type="email"
            fullWidth
            onChange={e => setEmail(e.target.value)}
          />
          {/* <hr/> */}
          <TextField
            variant='outlined'
            margin="dense"
            id="password"
            label="Password"
            type="email"
            fullWidth
            onChange={e => setPassword(e.target.value)}
          />

            <hr/>
            <DialogContentText>
            <h3>New to Crypto Collection? <a href="">Create an Account here.</a></h3>

          </DialogContentText>


        </DialogContent> 
        <hr/>
      <hr/>


      <hr/>
        <DialogActions>
          <button  style={{backgroundColor: 'gold', fontSize:'15px', borderRadius:'7px', padding: '10px'}} onClick={handleClose} >
          ❌ Cancel
          </button>
          <button style={{backgroundColor: 'gold',  fontSize:'15px', borderRadius:'7px', padding: '10px'}} 
            onClick={handleSubmit} >
           Login 🔑 🚪
          </button>
        </DialogActions>
      <hr/>
      <hr/>
      <hr/>
      <hr/>
      <hr/>
      </Dialog>
    </div>
    // </div>
  );
};


// Login.propTypes = {
//   setToken: PropTypes.func.isRequired
// }




export default Login;
