import React, { useEffect } from "react";
import "./CryptoDataDisplay.css";
import { connect } from "react-redux";
import cryptoDataReducer from "../../store/reducers/cryptoDataReducer";
import {
  setCryptoData,
  addCryptoData,
} from "../../store/actions/actionCreators";
//===================================================================================//
//===================================================================================//

const CryptoDataDisplay = (props) => {
  useEffect(() => {
    console.log(props);
  });

  return (
    <div className="crypto__data__display">
      {props.cryptoData.map((value) => {
        if(value.id === 'bitcoin'){

          return (

            <img src={value.image}/>
            )
          }
        console.log(value);
      })}
      <h1> Cryto Display</h1>
    </div>
  );
};

//===================================================================================//
//===================================================================================//
const mapStateToProps = (state) => {
  return { cryptoData: state.cryptoData };
};

export default connect(mapStateToProps, { setCryptoData, addCryptoData })(
  CryptoDataDisplay
);
